	var viable_assign = g_form.getValue('viable_assignment');
	//g_form.addInfoMessage(viable_assign);


//para ocultar una secci贸n se ocupa el metodo setSectionDisplay y se ocupa como parametro el nombre de la secci贸n, si este posee espacios, el primero se sustituye por un gui贸n bajo, y el resto de los espacios se elimina, todo debe de estar en minusculas

	if(viable_assign == "false")
	{
		g_form.setSectionDisplay('hiring_requirements', false);
